import React, { useState, useEffect } from "react";
import { Container, Form, Button, Alert, ListGroup } from "react-bootstrap";
import axios from "axios";
const ngrok = require('ngrok');


function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState(null);
  const [fileList, setFileList] = useState([]);

  useEffect(() => {
    (async () => {
      const url = await ngrok.connect({
        addr: port,
        subdomain: "your-ngrok-subdomain", // Replace with your ngrok subdomain
      });
      setNgrokUrl(url);
    })();
  }, []);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append("file", selectedFile);

      axios
        .post(
          "https://2948-2405-201-4014-2120-a22-705e-d1b1-6197.ngrok-free.app/upload",
          formData
        )
        .then((res) => {
          console.log(res.data);
          setUploadStatus("success");
          fetchFileList(); // Update the file list after successful upload
        })
        .catch((err) => {
          console.error(err);
          console.log(err.response);
          setUploadStatus("error");
        });
    }
  };

  const fetchFileList = () => {
    axios
      .get(`${ngrokUrl}/files`) // Use ngrokUrl variable in the API call
      .then((res) => {
        console.log(res.data);
        setFileList(res.data.files);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  const handleDownload = (fileName) => {
    window.open(
      `https://2948-2405-201-4014-2120-a22-705e-d1b1-6197.ngrok-free.app/download/${fileName}`,
      "_blank"
    );
  };

  return (
    <Container className="mt-5">
      <h1>File Upload</h1>
      {uploadStatus === "success" && (
        <Alert
          variant="success"
          onClose={() => setUploadStatus(null)}
          dismissible
        >
          File uploaded successfully.
        </Alert>
      )}
      {uploadStatus === "error" && (
        <Alert
          variant="danger"
          onClose={() => setUploadStatus(null)}
          dismissible
        >
          Error uploading file. Please try again.
        </Alert>
      )}
      <Form>
        <Form.Group controlId="formFile" className="mb-3">
          <Form.Label>Select a file</Form.Label>
          <Form.Control type="file" onChange={handleFileChange} />
        </Form.Group>
        <Button variant="primary" onClick={handleUpload}>
          Upload
        </Button>
      </Form>

      <h2>File List</h2>
      {fileList.length > 0 ? (
        <ListGroup>
          {fileList.map((fileName, index) => (
            <ListGroup.Item key={index}>
              <div>{fileName}</div>
              <Button
                variant="secondary"
                size="sm"
                onClick={() => handleDownload(fileName)}
              >
                Download
              </Button>
            </ListGroup.Item>
          ))}
        </ListGroup>
      ) : (
        <div>No files uploaded.</div>
      )}
    </Container>
  );
}

export default App;
